import ApiService from './ApiService';

const API_BASE_URL = 'http://localhost:8081';

const BlockService = {
  getBlocks: async () => {
    return ApiService.get(`${API_BASE_URL}/block`);
  },

  createBlock: async blockData => {
    return ApiService.post(`${API_BASE_URL}/block/create`, blockData);
  },

  updateBlock: async (oldBlock, newBlock) => {
    const response = await ApiService.put(`${API_BASE_URL}/block/update/${oldBlock}`, {
      newBlock: newBlock,
    });
    const responseData = await response.json();
    return responseData;
  },

  deleteBlock: async blockId => {
    return ApiService.delete(`${API_BASE_URL}/block?block=${blockId}`);
  },
};

export default BlockService;
