import ApiService from './ApiService';

const API_BASE_URL = 'http://localhost:8081';

const BlockService = {
  getBlocks: async () => {
    return ApiService.get(`${API_BASE_URL}/block`);
  },

  createBlock: async blockData => {
    try {
      const response = await ApiService.post(`${API_BASE_URL}/block/create`, blockData);
      if (response.status === 200) {
        return response.data;
      } else if (response.status === 400) {
        throw new Error('Bad Request');
      }
    } catch (error) {
      throw new Error('Error creating block');
    }
  },


  updateBlock: async (oldBlock, newBlock) => {
    try {
      const response = await ApiService.put(`${API_BASE_URL}/block/update/${oldBlock}`, {
        newBlock: newBlock,
      });
      if (response.status === 200) {
        return response.data;
      } else if (response.status === 400) {
        throw new Error('Bad Request');
      }
    } catch (error) {
      throw new Error('Error updating block');
    }
  },

  deleteBlock: async block => {
    try {
      const response = await ApiService.delete(`${API_BASE_URL}/block?block=${block}`);
      if (response.status === 200) {
        return response.data;
      } else if (response.status === 400) {
        throw new Error('Bad Request');
      }
    } catch (error) {
      throw new Error('Error deleting block');
    }
  },

};

export default BlockService;
