import React, { useState } from 'react';
import BlockService from '../services/BlockService';

function UpdateBlock() {
  const [blockId, setBlockId] = useState('');
  const [updatedThisBlock, setUpdatedThisBlock] = useState('');
  const [updatedNextBlock, setUpdatedNextBlock] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    await BlockService.updateBlock(blockId, {
      thisBlock: updatedThisBlock,
      nextBlock: updatedNextBlock,
    });
    // Reset form fields or perform other actions after updating
    setBlockId('');
    setUpdatedThisBlock('');
    setUpdatedNextBlock('');
  };

  return (
    <div>
      <h2>Update Block</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Block ID:
          <input
            type="text"
            value={blockId}
            onChange={e => setBlockId(e.target.value)}
          />
        </label>
        <br />
        <label>
          Updated ThisBlock:
          <input
            type="text"
            value={updatedThisBlock}
            onChange={e => setUpdatedThisBlock(e.target.value)}
          />
        </label>
        <br />
        <label>
          Updated NextBlock:
          <input
            type="text"
            value={updatedNextBlock}
            onChange={e => setUpdatedNextBlock(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default UpdateBlock;
